CREATE TABLE lodgingchain (
  chainId text PRIMARY KEY NOT NULL,
  chainName text,
  headquarters text,
  regionalPresence text,
  yearFounded INTEGER,
  numHotels INTEGER,
  numRooms INTEGER
);




CREATE TABLE brand (
  brandId INTEGER PRIMARY KEY NOT NULL,
  brandName text NOT NULL,
  brandSegment text,
  brandMinSqFt INTEGER,
  chainId text NOT NULL,
  FOREIGN KEY (chainId) REFERENCES lodgingchain(chainId)
);




CREATE TABLE hotel (
  hotelId INTEGER PRIMARY KEY NOT NULL,
  hotelName text NOT NULL,
  hotelStarRating REAL,
  hotelType text,
  hotelStreet text,
  hotelZIP INTEGER,
  hotelCity text,
  hotelState text,
  hotelRooms INTEGER,
  hotelUrl text,
  brandId INTEGER,
  employeeId INTEGER,
  FOREIGN KEY (brandId) REFERENCES brand (brandId)
);



CREATE TABLE author (
  authorId INTEGER PRIMARY KEY NOT NULL,
  authorNickname text,
  authorCity text,
  authorState text,
  authorCountry text,
  authorLevel INTEGER,
  authorYearBirth INTEGER
);




CREATE TABLE review (
  reviewId INTEGER PRIMARY KEY NOT NULL,
  reviewDate NUMERIC NOT NULL,
  reviewStayMonth text,
  reviewStayYear NUMERIC,
  reviewHelpfulness INTEGER,
  reviewMobile text,
  reviewOverall INTEGER,
  reviewCleanliness INTEGER,
  reviewLocation INTEGER,
  reviewRooms INTEGER,
  reviewService INTEGER,
  reviewValue INTEGER,
  reviewTitle text,
  reviewText text,
  hotelId INTEGER NOT NULL,
  authorId INTEGER NOT NULL,
  FOREIGN KEY (authorId) REFERENCES author (authorId),
  FOREIGN KEY (hotelId) REFERENCES hotel (hotelId)
);


